import React, { Component, Fragment } from 'react';
import Head from 'next/head'
import styled from 'styled-components';
import {
  typography,
  layout,
  space,
  color,
  display,
  flexbox,
  border,
} from 'styled-system'
import Dropzone from 'react-dropzone'

import Nav from '../components/Nav'
import Footer from '../components/Footer'

const PageWrapper = styled.div`

`;

const PageHeader = styled.div(
  {
    boxSizing: 'border-box',
  },
  color,
  layout,
  space,
  display,
  flexbox,
);

const PageTitle = styled.h1(
  {},
  typography,
  color,
  space,
);

const PageSubtitle = styled.h3(
  {},
  typography,
  color,
  space,
);

const PageContent = styled.div(
  {
    boxSizing: 'border-box',
  },
  space,
  display,
  flexbox,
);

const Form = styled.form(
  {
    boxSizing: 'boder-box'
  },
  layout,
  border,
  space,
  flexbox,
  display,
);

const Uploader = styled.div(
  {
    boxSizing: 'border-box',
  },
  border,
  color,
  layout,
  display,
  flexbox,
  space,
  color,
  typography,
);

const File = styled.div(
  {
    boxSizing: 'border-box'
  },
  display,
  flexbox,
  layout,
  space,
  color,
  border,
);

const Label = styled.label(
  {},
  typography,
  color,
  space,
);

const Input = styled.input(
  {
  },
  display,
  layout,
  space,
  border,
  typography,
);

const Textarea = styled.textarea(
  {},
  display,
  layout,
  space,
  border,
  typography,
);

const Submit = styled.input(
  {
    boxSizing: 'border-box',
    transition: 'background-color 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms',

    ':hover': {
      backgroundColor: '#fff',
      color: '#1a1a1a',
    }
  },
  border,
  flexbox,
  space,
  typography,
  color,
);

const Disclaimer = styled.span(
  {
    textAlign: 'center',

    '& a': {
      textDecoration: 'none',
    }
  },
  layout,
  typography,
  color,
);

class Onboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      name: "",
      menus: []
    }
  }

  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleSubmit(e) {
    e.preventDefault()
  }

  handleDrop(files) {
    console.log(files);
    let { menus } = this.state
    files.forEach((file) => menus.push(file))
    console.log(menus);
    this.setState({
      menus
    })
  }

  removeFile(i) {
    let { menus } = this.state
    menus.splice(i, 1)
    this.setState({
      menus
    })
  }

  render() {
    return (
      <PageWrapper>
        <Head>
          <title>
            Upload your menu | OurMenu
          </title>
          <meta name="viewport" content="width=device-width, initial-scale=0.86, maximum-scale=3.0, minimum-scale=0.86" />
          <link rel="apple-touch-icon" sizes="180x180" href="/static/apple-touch-icon.png"/>
          <link rel="icon" type="image/png" sizes="32x32" href="/static/favicon-32x32.png"/>
          <link rel="icon" type="image/png" sizes="16x16" href="/static/favicon-16x16.png"/>
          <link rel="manifest" href="/static/site.webmanifest"/>
          <link rel="mask-icon" href="/static/safari-pinned-tab.svg" color="#000"/>
          <link rel="shortcut icon" href="/static/favicon.ico"/>
          <meta name="msapplication-TileColor" content="#000000"/>
          <meta name="msapplication-config" content="/static/browserconfig.xml"/>
          <meta name="theme-color" content="#000"/>
        </Head>
        <Nav transparent/>
        <PageHeader
          width="100vw"
          minHeight="50vh"
          bg="blue"
          color="black"
          pt="196px"
          pl={['16px', '24px', '56px']}
          pr={['16px', '24px', '56px']}
          pb={['16px', '24px', '56px']}
          display="flex"
          alignItems="center"
        >
          <PageTitle
            fontSize="4.5rem"
            fontWeight="bold"
            lineHeight="5rem"
            color="black"
            m="0"
          >
            We'll do all the hard stuff.
            <br/>
            You sit back and relax.
          </PageTitle>
        </PageHeader>
        <PageContent
          display="flex"
          alignItems="flex-start"
          justifyContent="center"
          p={['16px', '24px', '56px']}
        >
          <Form
            width="100%"
            maxWidth="560px"
            border="2px solid"
            borderColor="black"
            borderRadius="8px"
            p="24px"
            display="flex"
            flexDirection="column"
            onSubmit={(e) => this.handleSubmit(e)}
          >
            <Dropzone
              multiple
              onDrop={acceptedFiles => this.handleDrop(acceptedFiles)}
            >
              {({getRootProps, getInputProps, isDragActive, isDragAccept, isDragReject}) => (
                <section>
                  <Uploader
                  border="2px solid"
                  borderColor="lightGrey"
                  borderRadius="8px"
                  borderStyle="dashed"
                  display="flex"
                  flexDirection="column"
                  alignItems="center"
                  justifyContent="center"
                  width="100%"
                  height="100%"
                  minHeight="160px"
                  p="48px"
                  mb="16px"
                  fontSize="1rem"
                  fontWeight="bold"
                  color="grey"
                  bg={isDragActive ? 'lightGrey' : 'white'}
                  {...getRootProps()}>
                    <input {...getInputProps()} />
                    Drop your menus here or click to upload
                  </Uploader>
                </section>
              )}
            </Dropzone>
            {this.state.menus.map((file, i) =>
              <File key={file.path}
                width="100%"
                display="inline-flex"
                justifyContent="space-between"
                alignItems="center"
                p="16px 24px"
                borderRadius="8px"
                bg="black"
                color="white"
                mb="8px"
              >
                <span>{file.path}</span>
                <span onClick={() => this.removeFile(i)}>x</span>
              </File>
            )}
            <Label
              fontSize=".875rem"
              fontWeight="bold"
              color="grey"
              mb="4px"
              htmlFor="name">
              Full name*
            </Label>
            <Input
              border="2px solid"
              borderColor="black"
              borderRadius="8px"
              display="flex"
              p="8px 16px"
              mb="16px"
              fontSize="1.25rem"
              fontWeight="regular"
              color="black"
              placeholder="Your name"
              type="text"
              autoComplete="on"
              required
              value={this.state.name}
              name="name"
              onChange={(e) => this.handleChange(e)}
            />
            <Label
              fontSize=".875rem"
              fontWeight="bold"
              color="grey"
              mb="4px"
              htmlFor="email">
              Email*
            </Label>
            <Input
              border="2px solid"
              borderColor="black"
              borderRadius="8px"
              display="flex"
              flex="1"
              p="8px 16px"
              mb="16px"
              fontSize="1.25rem"
              fontWeight="regular"
              color="black"
              placeholder="Your email"
              type="email"
              autoComplete="on"
              required
              value={this.state.email}
              name="email"
              onChange={(e) => this.handleChange(e)}
            />
            <Submit
              border="2px solid"
              borderRadius='8px'
              borderColor="black"
              p="16px 24px"
              fontSize="1rem"
              fontWeight="bold"
              color="black"
              bg="blue"
              type="submit"
              value="Upload your menu"
              mb="8px"
            />
            <Disclaimer
              width="100%"
              fontSize="0.875rem"
              fontWeight="regular"
              color="grey"
            >
              By uploading your menu to OurMenu, you are accepting our Terms and Privacy Policy.
              </Disclaimer>
          </Form>
        </PageContent>
        <Footer />
      </PageWrapper>
    );
  }

}

export default Onboard;
