import React, { Component } from 'react';
import Head from 'next/head'
import styled from 'styled-components';
import {
  typography,
  layout,
  space,
  color,
  display,
  flexbox,
  border,
} from 'styled-system'
import { withRouter } from 'next/router';
import Dropzone from 'react-dropzone'
import base from '../config'
import firebase from 'firebase/app'
import 'firebase/firestore'
import 'firebase/storage'

import Nav from '../components/Nav'
import Footer from '../components/Footer'

var storageRef = firebase.storage().ref();

const PageWrapper = styled.div`

`;

const PageHeader = styled.div(
  {
    boxSizing: 'border-box',
  },
  color,
  layout,
  space,
  display,
  flexbox,
);

const PageTitle = styled.h1(
  {},
  typography,
  color,
  space,
);

const PageSubtitle = styled.h3(
  {},
  typography,
  color,
  space,
);

const PageContent = styled.div(
  {
    boxSizing: 'border-box',
  },
  space,
  display,
  flexbox,
);

const Form = styled.form(
  {
    boxSizing: 'boder-box'
  },
  layout,
  border,
  space,
  flexbox,
  display,
);

const Uploader = styled.div(
  {
    boxSizing: 'border-box',
  },
  border,
  color,
  layout,
  display,
  flexbox,
  space,
  color,
  typography,
);

const File = styled.div(
  {
    boxSizing: 'border-box',

    '& span': {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    }
  },
  display,
  flexbox,
  layout,
  space,
  color,
  border,
);

const Label = styled.label(
  {},
  typography,
  color,
  space,
);

const Input = styled.input(
  {
  },
  display,
  layout,
  space,
  border,
  typography,
);

const Textarea = styled.textarea(
  {},
  display,
  layout,
  space,
  border,
  typography,
);

const Submit = styled.input(
  {
    boxSizing: 'border-box',
    transition: 'background-color 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms',

    ':hover': {
      backgroundColor: '#fff',
      color: '#1a1a1a',
    }
  },
  border,
  flexbox,
  space,
  typography,
  color,
);

class RequestDemo extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: this.props.router.query.email || "",
      name: "",
      menus: []
    }
  }

  componentDidMount() {
    console.log(this.props.router.query);
  }

  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleSubmit(e) {
    e.preventDefault()
    const { name, email, menus } = this.state
    const data = {
      name,
      email,
      menus: []
    }
    base.addToCollection('users', data)
      .then((data) => {
        console.log(data.id)
        if (menus.length > 0) {
          this.handleUpload(data.id)
        }
      }).catch(err => {
      console.log(err);
    });
  }

  handleUpload(id) {
    const files = this.state.menus
    let downloadLinks = []
    files.forEach(function(file) {
      var metadata = {
        contentType: 'image/jpeg'
      };
      var uploadTask = storageRef.child(`users/${id}/menus`).put(file, metadata);
      downloadLinks.push("test")
      console.log(downloadLinks);
      if (downloadLinks.length === files.length) {
        console.log('files uploaded');
      }
    //   uploadTask.on(firebase.storage.TaskEvent.STATE_CHANGED, // or 'state_changed'
    //   function(snapshot) {
    //     // Get task progress, including the number of bytes uploaded and the total number of bytes to be uploaded
    //     var progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
    //     console.log('Upload is ' + progress + '% done');
    //     switch (snapshot.state) {
    //       case firebase.storage.TaskState.PAUSED: // or 'paused'
    //         console.log('Upload is paused');
    //         break;
    //       case firebase.storage.TaskState.RUNNING: // or 'running'
    //         console.log('Upload is running');
    //         break;
    //     }
    //   }, function(error) {
    //
    //   // A full list of error codes is available at
    //   // https://firebase.google.com/docs/storage/web/handle-errors
    //   switch (error.code) {
    //     case 'storage/unauthorized':
    //       console.log('unauthorized');
    //       break;
    //
    //     case 'storage/canceled':
    //       console.log('canceled');
    //       break;
    //
    //     case 'storage/unknown':
    //       console.log('idk');
    //       break;
    //   }
    // }, function() {
    //   // Upload completed successfully, now we can get the download URL
    //   uploadTask.snapshot.ref.getDownloadURL().then(function(downloadURL) {
    //     downloadLinks.push(downloadURL)
    //   });
    // });
    })
  }

  handleDrop(files) {
    console.log(files);
    let { menus } = this.state
    files.forEach((file) => menus.push(file))
    console.log(menus);
    this.setState({
      menus
    })
  }

  removeFile(i) {
    let { menus } = this.state
    menus.splice(i, 1)
    this.setState({
      menus
    })
  }

  render() {
    return (
      <PageWrapper>
        <Head>
          <title>
            Get a free custom demo | OurMenu
          </title>
          <meta name="viewport" content="width=device-width, initial-scale=0.86, maximum-scale=3.0, minimum-scale=0.86" />
          <link rel="apple-touch-icon" sizes="180x180" href="/static/apple-touch-icon.png"/>
          <link rel="icon" type="image/png" sizes="32x32" href="/static/favicon-32x32.png"/>
