import React, { Component, Fragment } from 'react';
import Head from 'next/head'
import styled from 'styled-components';
import {
  typography,
  layout,
  space,
  color,
  display,
  flexbox,
  border,
} from 'styled-system'
import { withRouter } from 'next/router';
import Dropzone from 'react-dropzone'
import base from '../config'
import firebase from 'firebase/app'
import 'firebase/firestore'
import 'firebase/storage'

import Nav from '../components/Nav'
import Footer from '../components/Footer'

var storageRef = firebase.storage().ref();

const PageWrapper = styled.div`

`;

const PageHeader = styled.div(
  {
    boxSizing: 'border-box',
  },
  color,
  layout,
  space,
  display,
  flexbox,
);

const PageTitle = styled.h1(
  {},
  typography,
  color,
  space,
);

const PageSubtitle = styled.h3(
  {},
  typography,
  color,
  space,
);

const PageContent = styled.div(
  {
    boxSizing: 'border-box',
  },
  space,
  display,
  flexbox,
);

const Form = styled.form(
  {
    boxSizing: 'boder-box'
  },
  layout,
  border,
  space,
  flexbox,
  display,
);

const Uploader = styled.div(
  {
    boxSizing: 'border-box',
  },
  border,
  color,
  layout,
  display,
  flexbox,
  space,
  color,
  typography,
);

const File = styled.div(
  {
    boxSizing: 'border-box',

    '& span': {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    }
  },
  display,
  flexbox,
  layout,
  space,
  color,
  border,
);

const Label = styled.label(
  {},
  typography,
  color,
  space,
);

const Input = styled.input(
  {
  },
  display,
  layout,
  space,
  border,
  typography,
);

const Textarea = styled.textarea(
  {},
  display,
  layout,
  space,
  border,
  typography,
);

const Submit = styled.input(
  {
    boxSizing: 'border-box',
    transition: 'background-color 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms',

    ':hover': {
      backgroundColor: '#fff',
      color: '#1a1a1a',
    }
  },
  border,
  flexbox,
  space,
  typography,
  color,
);

const Success = styled.div(
  {},
  display,
  flexbox,
);

const SuccessEmoji = styled.span(
  {},
  typography,
  display,
  flexbox,
);

const SuccessTitle = styled.h4(
  {},
  typography,
  display,
  flexbox,
  color,
  space,
);

const SuccessSubtitle = styled.span(
  {},
  typography,
  display,
  flexbox,
  color,
  space,
);

class RequestDemo extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: this.props.router.query.email || "",
      name: "",
      menus: [],
      isSuccess: false,
    }
  }

  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleSubmit(e) {
    e.preventDefault()
    const { name, email, menus } = this.state
    const data = {
      name,
      email,
      menus: []
    }
    base.addToCollection('users', data)
      .then((data) => {
        console.log(data.id)
        if (menus.length > 0) {
          this.handleUpload(data.id)
        }
      }).catch(err => {
      console.log(err);
    });
  }

  handleUpload(id) {
    const files = this.state.menus
    let downloadLinks = []
    const self = this
    files.forEach(function(file) {
      var metadata = {
        contentType: 'image/jpeg'
      };
      var uploadTask = storageRef.child(`users/${id}/menus/${file.name}`).put(file, metadata);
      uploadTask.on(firebase.storage.TaskEvent.STATE_CHANGED, // or 'state_changed'
      function(snapshot) {
        // Get task progress, including the number of bytes uploaded and the total number of bytes to be uploaded
        var progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
        console.log('Upload is ' + progress + '% done');
        switch (snapshot.state) {
          case firebase.storage.TaskState.PAUSED: // or 'paused'
            console.log('Upload is paused');
            break;
          case firebase.storage.TaskState.RUNNING: // or 'running'
            console.log('Upload is running');
            break;
        }
      }, function(error) {

      // A full list of error codes is available at
      // https://firebase.google.com/docs/storage/web/handle-errors
      switch (error.code) {
        case 'storage/unauthorized':
          console.log('unauthorized');
          break;

        case 'storage/canceled':
